function SortDataInto4(data) {
  const dataFinal = []
  const newDataSort = [...data]

  let counter = 0;
  // För varje 4 element så gruppera 
  let arrayTemp = []
  for (let i = 0; i < newDataSort.length; i++) {
    if(counter === 4) {
      dataFinal.push(arrayTemp)
      arrayTemp = [];
      counter = 0
    }
    else if(i === newDataSort.length - 1) {
      arrayTemp.push(newDataSort[i])
      dataFinal.push(arrayTemp);
      break;
    }
    arrayTemp.push(newDataSort[i])
    counter++; 
  }
  return dataFinal
}

